package week1.day2;

public class SwitchCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age =18;
		switch (age) {
		case 17:
			System.out.println("under age for Voting");
			break;
		case 18:
			System.out.println("age for voting");
			break;
			default:
				System.out.println("No voting");
				break;
		}

	}

}
